<?phpabstract class Hero{    protected string $name;    protected int $health;    protected int $level = 1;    public function __construct(string $name,int $level)    {        $this->name = $name;        $this->health = 100;        $this->level = $level ?? 0;    }    public function display()    {        return "Name : " . $this->name . " Health: " . $this->health;    }    public function warCry(){        return $this->name . " attacks ";    }    public function getHealth(): int    {        return $this->health;    }    public function displayName(){        return $this->name;    }    public function setDamage(int $damage):void    {        $this->health -= max($damage,0);    }    public function canAttack():bool{        return $this->health > 1;    }    //We are all humans and can we make mistakes    public function attackMiss(){        $random = rand(0,5);        if($random === 5){            return true;        }    }    public function levelUp():void{        $this->level+= 1;        $this->health += round(0.5 * $this->level);    }    public function getLevel():int{        return $this->level;    }}